import './App.scss';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container} from "react-bootstrap";
import React, {useState, useEffect} from "react";
import {testData} from "./data";
import mreLogo from "./mre.png";
import axios from "axios";
import warningImg from "./warning.png";

const TextPair = ({top, bottom}) => {
  return (
    <>
      <p className="mt-0 mb-0">
        {top}
      </p>
      <p className="mt-0 mb-0">
        <b>
          {bottom}
        </b>
      </p>
    </>
  );
}

const Loading = () => {

  const [dots, setDots] = useState("");

  useEffect(() => {
    let interval = setInterval(() => {
      setDots((curDots) => curDots.length === 5 ? "." : curDots + ".");
    }, 350);

    return () => clearInterval(interval);
  }, []);

  return (
    <h1 className="loading-text">Loading recent data{dots}</h1>
  );
}

export const App = () => {

  const [parts, setParts] = useState([]);
  const [err, setErr] = useState(false);
  const [loading, setLoading] = useState(true);
  const [lastRefresh, setLastRefresh] = useState(null);

  let args = window.location.href.split("/");
  let resource = args.length === 0 || args[0] === "" ? null : args[args.length - 1];

  useEffect(() => {
    refresh();
  }, []);


  if(resource === null || resource === "") { 
    return <h1>Please specify a resource!</h1>
  }
  const refresh = () => {
    let dev = true;
    let url = dev ? "http://localhost:9009/" : "/"; 
    axios.get(url + "tracker/" + resource).then((res) => {
        setParts(res.data);
        setLoading(false);
        setErr(false);
        setLastRefresh(new Date());
    }).catch((err) => {
        setErr(true);
        setLoading(false);
    });
  }

  return (
    <div className="App">
        <div className="cell-label">
          <img className="cell-label-image" src={mreLogo} />
          <h1 className="cell-label-text">Resource: <b>{resource}</b></h1>
        </div>

        <Container fluid>
          {!err ? null : (
            <div className="error">
              <p className="mb-0 error-text"><img src={warningImg} className="warning-icon" /><b>Warning: Unable to refresh data. This data may not reflect recent changes.</b> {lastRefresh === null ? null : "Last successful refresh: " + lastRefresh.toLocaleString()}</p>
            </div>
          )}
          <div className="row mt-4">
            <div className="col-10 mx-auto">
              {parts.map((part) => {

                let quants = part.Quants.slice(0, 7);

                return (
                  <>
                    <div className="row mt-4">
                      <div className="col-12 part-header-info pt-2 pb-2">
                        <div className="row">
                          <div className="col-3">
                            <TextPair top="Part #" bottom={part.PartNo} />
                          </div>
                          <div className="col-6">
                            <TextPair top="Description" bottom={part.Description} />
                          </div>
                          <div className="col-3">
                            <TextPair top="Quantity on Hand (QOH)" bottom={part.QOH} />
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="eigth-row mt-2 row">
                      <div className="eigth projected-title pt-2 pb-2">
                          <p className="mb-0 mt-1">End of Day QOH</p>
                      </div>
                      {quants.map((q) => {
                          return (
                              <div className={"eigth projected-qoh pt-2 pb-2 " + (q.MinQOH > 0 ? "good" : "bad")}>
                                  <p className="mb-0 white-text"><b>{q.MinQOH}</b></p>
                              </div>
                          );
                      })}
                    </div>
                    <div className="eigth-row mt-2 row">
                      <div className="eigth"></div>
                      {quants.map((q) => {
                        let d = new Date(q.Date);
                        const dayNames = ["Sun", "Mon", "Tues", "Wed", "Thurs", "Fri", "Sat"];
                        const months = ["Jan", "Feb", "Mar", "Apr", "May", "June", "July", "Aug", "Sep", "Oct", "Nov", "Dec"];
                        const weekDay = dayNames[d.getDay()];
                        const month = months[d.getMonth()];
                        const dayOfMonth = d.getUTCDate();

                          return (
                              <div className={"eigth qoh-date pb-2"}>
                                  <p className="mb-0 text-center"><b>{weekDay}</b>&nbsp;{dayOfMonth}&nbsp;{month}</p>
                              </div>
                          );
                      })}
                    </div>
                  </>
                );
              })}
            </div>
            {
              !loading ? null : (
                <div className="row mt-5">
                    <div className="col-12 text-center">
                      <Loading />
                    </div>
                </div>
              )
            }
          </div>
        </Container>
    </div>
  );
}
