import './App.scss';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container} from "react-bootstrap";
import React, {useState, useEffect} from "react";
import {testData} from "./data";

const TextPair = ({top, bottom}) => {
  return (
    <>
      <p className="mt-0 mb-0">
        {top}
      </p>
      <p className="mt-0 mb-0">
        <b>
          {bottom}
        </b>
      </p>
    </>
  );
}

export const App = () => {

  const [parts, setParts] = useState(testData);

  let args = window.location.href.split("/");
  let resource = args.length === 0 || args[0] === "" ? null : args[args.length - 1];

  if(resource === null || resource === "") { 
    return <h1>Please specify a resource!</h1>
  }

  return (
    <div className="App">
        <div className="cell-label">
          <h1 className="cell-label-text">Cell: <b>{resource}</b></h1>
        </div>

        <Container fluid>
          <div className="row mt-4">
            <div className="col-10 mx-auto">
              {parts.map((part) => {

                let quants = part.Quants.slice(0, 7);

                return (
                  <>
                    <div className="row mt-4">
                      <div className="col-12 part-header-info pt-2 pb-2">
                        <div className="row">
                          <div className="col-3">
                            <TextPair top="Part #" bottom={part.PartNo} />
                          </div>
                          <div className="col-6">
                            <TextPair top="Description" bottom={part.Description} />
                          </div>
                          <div className="col-3">
                            <TextPair top="Quantity on Hand (QOH)" bottom={part.QOH} />
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="eigth-row mt-2 row">
                      <div className="eigth projected-title pt-2 pb-2">
                          <p className="mb-0 mt-1">End of Day QOH</p>
                      </div>
                      {quants.map((q) => {
                          return (
                              <div className={"eigth projected-qoh pt-2 pb-2 " + (q.MinQOH > 0 ? "good" : "bad")}>
                                  <p className="mb-0 white-text"><b>{q.MinQOH}</b></p>
                              </div>
                          );
                      })}
                    </div>
                    <div className="eigth-row mt-2 row">
                      <div className="eigth"></div>
                      {quants.map((q) => {
                        let date = Date.parse(q.Date);
                        const dayName = ["Sun", "Mon", "Tues", "Wed", "Thurs", "Fri", "Sat"];
                        const months = ["Jan", "Feb", "Mar", "Apr", "May", "June", "July", "Aug", "Sep", "Oct", "Nov", "Dec"];
                        const weekDay = dayName(date.getDay());
                        const month = months[date.getMonth()];
                        const dayOfMonth = date.getUTCDate();

                          return (
                              <div className={"eigth qoh-date pb-2"}>
                                  <p className="mb-0 text-center">{q.Date}</p>
                              </div>
                          );
                      })}
                    </div>
                  </>
                );
              })}
            </div>
          </div>
        </Container>
    </div>
  );
}
